package ru.ystu.labs_store.model;import org.springframework.validation.annotation.Validated;import javax.persistence.*;@Validated@Entity@Table(name = "cart")public class Cart {	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	private int id;	@ManyToOne	@JoinColumn(name = "id_product", nullable = false)	private Product product;	@ManyToOne	@JoinColumn(name = "id_user", nullable = false)	private User user;	private int count;	@ManyToOne	@JoinColumn(name = "id_archive")	private History idArchive;	public Cart() {		this.count = 1;	}	public Cart(Product product, User user) {		this.product = product;		this.count = 1;		this.user = user;		this.idArchive = null;	}	public void setId(int id) {		this.id = id;	}	public int getId() {		return id;	}	public void setProduct(Product product) {		this.product = product;	}	public Product getProduct() {		return product;	}	public void setCount(int count) {		this.count = count;	}	public int getCount() {		return count;	}	public User getUser() {		return user;	}	public void setUser(User user) {		this.user = user;	}	public void setIdArchive(History idArchived) {		this.idArchive = idArchived;	}	public History getIdArchive() {		return idArchive;	}}