package ru.ystu.labs_store.model;import org.springframework.validation.annotation.Validated;import javax.persistence.*;import java.text.DecimalFormat;@Validated@Entity@Table(name = "product")public class Product {	public Product() {	}	public Product(Category category) {		this.category = category;	}	public Product(String name, String description, Category category, Double price, String image) {		this.name = name;		this.description = description;		this.category = category;		this.price = price;		this.image = image;	}	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	private int id;	private String name;	private String description;	@ManyToOne	@JoinColumn(name = "id_category", nullable = false)	private Category category;	private Double price;	private String image;	public int getId() {		return id;	}	public String getName() {		return name;	}	public String getDescription() {		return description;	}	public Category getCategory() {		return category;	}	public Double getPrice() {		return price;	}	public String getImage() {		return image;	}	public void setId(int id) {		this.id = id;	}	public void setName(String name) {		this.name = name;	}	public void setDescription(String description) {		this.description = description;	}	public void setCategory(Category category) {		this.category = category;	}	public void setImage(String image) {		this.image = image;	}	public void setPrice(Double price) {		this.price = price;	}	public String getHandlePrice() {		DecimalFormat format = new DecimalFormat("###,###,##0.#");		return format.format(price) + " â‚½";	}}