package ru.ystu.labs_store.model;import org.springframework.validation.annotation.Validated;import javax.persistence.*;@Validated@Entity@Table(name = "user")public class User {	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	private int id;	private String login;	private String password;	public User() {		this.login = "";		this.password = "";	}	public void setId(int id) {		this.id = id;	}	public void setLogin(String login) {		this.login = login;	}	public void setPassword(String password) {		this.password = password;	}	public int getId() {		return id;	}	public String getLogin() {		return login;	}	public String getPassword() {		return password;	}}