create database store;use store;create table category(    id int auto_increment,    name text not null,    constraint id    unique (id));create table product(    id int auto_increment primary key ,    name text null,    description text null,    id_category int null,    price double null,    image text null,    constraint product_id_uindex        unique (id),    constraint product_ibfk_1        foreign key (id_category) references store.category (id));create table user(    id       int auto_increment primary key,    login    text null,    password text null,    constraint user_id_uindex        unique (id));create table cart(    id  int auto_increment primary key,    id_product int not null,    id_user    int not null,    count      int null,    id_archive int null,    constraint cart_ibfk_1        foreign key (id_product) references product (id),    constraint cart_ibfk_2        foreign key (id_archive) references history (id)            on delete set null,    constraint cart_user_id_fk        foreign key (id_user) references user (id));create table history(    id int auto_increment        primary key,    price double null,    date_time_order text null,    id_user int not null,    constraint history_user_id_fk        foreign key (id_user) references user (id));INSERT INTO store.category (name)VALUES ('Electronics and appliances');INSERT INTO store.category (name)VALUES ('Clothing and footwear');INSERT INTO store.category (name)VALUES ('Computers and accessories parts');INSERT INTO store.category (name)VALUES ('Mobile phones');INSERT INTO store.category (name)VALUES ('Home');INSERT INTO store.category (name)VALUES ('Baby products');INSERT INTO store.category (name)VALUES ('Furniture');INSERT INTO store.category (name)VALUES ('Building');INSERT INTO store.category (name)VALUES ('Sports');INSERT INTO store.category (name)VALUES ('Tourism and fishing');INSERT INTO store.category (name)VAL